import java.util.Scanner;

public class  LeftDownTriangle_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Enter number: ");

		
		for(int i=1; i<=4; i++) 
		{
			for(int j=i; j<=4; j++) 
			{
				
					System.out.print(" * ");
		
			}
				System.out.println(" ");
		
		}
	}

}
